-- Insert 100 users into the users table

-- Insert sample user_points for some users (optional - for testing leaderboard)
SELECT 
  u.id,
  FLOOR(RANDOM() * 10000) + 100, -- Random points between 100-10100
  FLOOR(RANDOM() * 5000) + 50, -- Random USDA between 50-5050
  'twitter_engagement',
  NOW() - INTERVAL '1 day' * FLOOR(RANDOM() * 30) -- Random date within last 30 days
FROM public.users u
WHERE u.username IN (
  'crypto_enthusiast', 'blockchain_dev', 'defi_trader', 'nft_collector', 'web3_builder',
  'crypto_analyst', 'token_investor', 'smart_contract', 'yield_farmer', 'dao_member',
  'crypto_whale', 'defi_protocol', 'liquidity_provider', 'staking_validator', 'governance_voter'
); 